const hre = require("hardhat");

async function main() {
  const [deployer] = await hre.ethers.getSigners();

  console.log("Deploying contracts with the account:", deployer.address);

  const Channel = await hre.ethers.getContractFactory("Channel");
  const channel = await Channel.deploy("0xf831d23BabEF7e494701442795d860EaFe03c6b3");

  await channel.deployed();

  console.log("Channel deployed to:", channel.address);
}

main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});