const hre = require("hardhat");

async function main() {
  const channelAddress = "0xYourDeployedContractAddressHere";
  const Channel = await hre.ethers.getContractAt("Channel", channelAddress);

  const tx = await Channel.mint(
    "0xd9145CCE52D386f254917e481eB44e9943F39138", // Destinataire
    hre.ethers.utils.parseUnits("500000", 6), // Montant (100 USDT)
    hre.ethers.utils.formatBytes32String("hash123") // Hash
  );

  console.log("Transaction hash:", tx.hash);
}

main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});