import hre from "hardhat";

async function main() {
  const tOutAssetAddress = "0xAdresseDuContratDéployé";
  const TOutAsset = await hre.ethers.getContractAt("TOutAsset", tOutAssetAddress);

  const tx = await TOutAsset.setAccount("0xAdresseDeSortie");
  console.log("Transaction hash pour setAccount :", tx.hash);
}

main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});